package oneDimensionalArray;

public class CountValuesFromArray {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int arr[]= {4,3,8,7,6,4,25,78,62,45};
		int count=0;
		
		for(int i=0;i<arr.length;i++)
		{
			count++;
			
		}
		System.out.println("the total elements in array:"+count);
	}

}
